import React from 'react';
import './App.css';

function Game({match}) {
  React.useEffect(() => {
    fetchStage();
  }, []);

  const [stage,setStage] = React.useState({});
  const [answer,setAnswer] = React.useState({});
  const [count,setCount] = React.useState(0);

  const fetchStage = async () => {
    const fetchStage = await fetch('http:localhost:8080/api/stage/${match.params.stageId}');
    const stage = await fetchStage.json();
    setAnswer(stage.data.answer);
    console.log(answer);
  }

  function handleClick() {
    setCount(count + 1);
  }

  return (
    <div>
        <h1>Guessing Game</h1>
        <p font="16" >Please Choose A or B or C or D to guess the first character</p>
        <p class="answer" font="16">Answer: </p>
        <p class="remainchar" font="16">Character(s)remaining: </p>
        <p class="countfail" font="16">Fail: {count}</p>    
        <p class="Choose" font="16" style={{display :"inline-block"}}>Choose: </p>
        <ul>
          <li>{answer[0]}</li>
        </ul>
        <button type='button' onClick={handleClick}>A</button>
        <button>B</button> 
        <button>C</button> 
        <button>D</button> 
        </div>
  );
}

export default Game;
