import React from 'react';

function Game({
  match
}) {
  React.useEffect(() => {
    fetchStage();
  }, []);

  const [stage, setStage] = React.useState({});
  const [answer, setAnswer] = React.useState({});
  const [count, setCount] = React.useState({});

  const fetchStage = async () => {
    const fetchStage = await fetch(`http:localhost:8080/api/stage/${match.params.stageId}`);
    const stage = await fetchStage.json();
    setAnswer(stage.data.answer);
  };

  return ( <
    div >
    <
    h1 > Guessing Game < /h1> <
    p font = "16" > Please Choose A or B or C or D to guess the first character < /p> <
    p className = "answer"
    font = "16" > Answer: < /p> <
    p className = "remainchar"
    font = "16" > Character(s) remaining: < /p> <
    p className = "countfail"
    font = "16" > Fail: < /p>     <
    p className = "Choose"
    font = "16"
    style = {
      {
        display: "inline-block"
      }
    } > Choose: < /p> <
    ul >
    <
    li > {
      String(answer[0])
    } < /li> <
    /ul> <
    button type = 'button' > A < /button> <
    button > B < /button>  <
    button > C < /button>  <
    button > D < /button>  <
    /div>
  );
}


export default Game;
