import React from "react";
import { Route } from "react-router-dom";
import Game from "./Game";
import "./App.css";
import Axios from "axios";

function Creategame() {
  const [list, setList] = React.useState([]);
  const [num,setNum] = React.useState(0);

  function postgame() {
    fetch("http://localhost:8080/api/stage", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ answer: list }),
    }).then(async (response) => {
      const data = await response.json();
      this.props.history.push(`http://localhost/Game/${data.id}`)
    }).then()
  }

  function add(char) {
    if(num <= 3){
    setList(list.concat(char));
    setNum(num + 1);
  }}

  return (
    <div>
      <h1>Create Guessing Game</h1>
      <p>Please Choose A or B or C or D to be answer (4 character) </p>
      <p className="answer">Answer is : {list[0] !=null ? list[0] : ""} {list[1] !=null ? list[1] : ""} {list[2] !=null ? list[2] : ""} {list[3] !=null ? list[3] : ""}  </p>
      <p className="Choose" style={{ display: "inline-block" }}>
        Choose:{""}
      </p>
      <button onClick={() => add("A")}>A</button>
      <button onClick={() => add("B")}>B</button>
      <button onClick={() => add("C")}>C</button>
      <button onClick={() => add("D")}>D</button>
      <p> Create</p>
      <button onClick={() => postgame()}>Submit</button>
    </div>
  );
}

export default Creategame;
